const leftContainer = document.getElementById("left");
const fruitForm = document.getElementById("fruit-form");
const selectedImg = document.getElementById("selected-img");
const selectedDesc = document.getElementById("selected-desc");
const changeColorBtn = document.getElementById("change-color");

const colors = ["#FF0000", "#FF4500", "#FF8C00", "#FFD700", "#ADFF2F", "#32CD32", "#00FA9A", "#00CED1", "#1E90FF", "#4169E1", "#8A2BE2", "#9400D3", "#FF1493", "#FF69B4", "#FFB6C1", "#FFDAB9"];


function updateFruitDisplay() {
    leftContainer.innerHTML = "";
    fruits.forEach(fruit => {
        const img = document.createElement("img");
        img.src = fruit.imageUrl;
        img.alt = fruit.name;
        img.addEventListener("click", () => selectFruit(fruit));
        leftContainer.appendChild(img);
    });
}

function selectFruit(fruit) {
    selectedImg.src = fruit.imageUrl;
    selectedDesc.innerHTML = `<strong>${fruit.name}</strong><br>${fruit.description}<br>Grams: ${fruit.grams}g<br>Price: $${fruit.price}/g<br>Rating: ${fruit.rating}<br>Comments: ${fruit.comments}`;
}
fruitForm.addEventListener("submit", function (e) {
    e.preventDefault();
    const name = document.getElementById("name").value;
    const description = document.getElementById("description").value;
    const grams = document.getElementById("grams").value;
    const price = document.getElementById("price").value;
    const rating = document.getElementById("rating").value;
    const comments = document.getElementById("comments").value;
    const imageUrl = document.getElementById("image-url").value;

    const fruit = { name, description, grams, price, rating, comments, imageUrl };
    fruits.push(fruit);
    updateFruitDisplay();
    fruitForm.reset();
});


changeColorBtn.addEventListener("click", function () {
    document.body.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
});

updateFruitDisplay();


